    upAxis = "Z"
)

def Xform "World"
{
    def Cylinder "YellowCylinder_1"
    {
        double height = 2.0
        double radius = 1.0
        double3 xformOp:translate = (2, 0, 0)
        double3 xformOp:scale = (1.0, 1.0, 1.0)
